ALTER PROCEDURE SHOW_FILES (
    TORRENT_ID INTEGER,
    DIR_LEVEL SMALLINT,
    PARENT_DIR INTEGER)
RETURNS (
    DIRFILE_ITEM VARCHAR(300),
    ITEM_SIZE INTEGER)
AS
declare variable I integer;
declare variable LEVEL_STR varchar(10);
declare variable PARENT_ID integer;
declare variable IS_EMPTY smallint;
begin
  LEVEL_STR='';
  i=:dir_level;
  while (:i > 0)do
  begin
    LEVEL_STR = :LEVEL_STR || '\-- ';
    i = :i - 1;
  end

  IS_EMPTY = 0;
  if (:PARENT_DIR is null) then
  begin
    for select :LEVEL_STR || '[' || d.dir_name || ']', d.dir_id, null from directoryes d where d.torrent_id = :TORRENT_ID and d.parent_id is null order by d.dir_name
    into :DIRFILE_ITEM, :PARENT_ID, :ITEM_SIZE do
    begin
      IS_EMPTY = 1;
      suspend;
      for select :LEVEL_STR || '\-- ' || f.file_name, f.file_size / 1024 from files f where f.torrent_id = :torrent_id and f.dir_id = :PARENT_ID order by f.file_name
      into :DIRFILE_ITEM, :ITEM_SIZE do
        suspend;
    end
  end
  else
  begin
    for select :LEVEL_STR || '[' || d.dir_name || ']', d.dir_id, null from directoryes d where d.torrent_id = :TORRENT_ID and d.parent_id = :PARENT_DIR order by d.dir_name
    into :DIRFILE_ITEM, :PARENT_ID, :ITEM_SIZE do
    begin
      IS_EMPTY = 1;
      suspend;
      for select :LEVEL_STR || '\-- ' || f.file_name, f.file_size / 1024 from files f where f.torrent_id = :torrent_id and f.dir_id = :PARENT_ID order by f.file_name
      into :DIRFILE_ITEM, :ITEM_SIZE do
        suspend;
    end
  end

  if (:IS_EMPTY = 1) then
    for select p.dirfile_item, p.item_size from SHOW_FILES(:torrent_id, :dir_level + 1, :parent_id) p into :DIRFILE_ITEM, :ITEM_SIZE do suspend;
end^



